import React from "react";

function PostModal({ data }) {
  return (
    <div className="absolute z-10 min-h-[100vh] text-white bg-[#222222e7] flex justify-center w-full ">
      <div className="flex flex-col items-center bg-[#1f1d1d] w-[800px] fit border">
        <h1>{data.title}</h1>
        <h1>{data.description}</h1>
        <img src={data.urlToImage} alt="" />
        <h1>{data.content}</h1>
      </div>
    </div>
  );
}

export default PostModal;
